pragma solidity ^0.4.23;
import "openzeppelin-solidity/contracts/ownership/Ownable.sol";

contract SocialPressure is Ownable {

    struct PledgeStruct {
        uint expiresAt;  //timestamp 
        uint expectedNumberOfActions; // make certain number of bytes
        bytes32 title;  // lookup bytes vs string and how it compares
        string details;
        uint collateral; 
        uint index;
    }

    mapping(address => PledgeStruct) private pledgeStructs;
    address[] private pledgeIndex;

    event NewPledge(address indexed userAddress, uint index, bytes32 title);

    function createPledge(
        uint _expiresAt,
        uint _expectedNumberOfActions,
        bytes32 _title,
        string _details
    ) 
        public 
        payable 
        returns(uint index)
    {
        pledgeStructs[msg.sender].expiresAt = _expiresAt;
        pledgeStructs[msg.sender].expectedNumberOfActions = _expectedNumberOfActions;
        pledgeStructs[msg.sender].title = _title;
        pledgeStructs[msg.sender].details = _details;
        pledgeStructs[msg.sender].collateral = msg.value;
        pledgeStructs[msg.sender].index = pledgeIndex.push(msg.sender) - 1;

        emit NewPledge(msg.sender, pledgeStructs[msg.sender].index, _title);
        return pledgeIndex.length - 1;
    }
}
